export default class Books extends React.Component {
    state = {
      books: []
    }

    componentDidMount() {
      axios.get('/library')
        .then(res => {
          const books = res.data;
          this.setState({ books });
        })
    }

    render() {
      return (
        <ul>
          { this.state.books.map(book => <li>{book.title}</li>)}
        </ul>
      )
    }
  }
