import React, { Component } from 'react'
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import './app.css'

class BooksComponent extends Component{

    constructor(props){
        super(props)
        this.state ={
            booksData: [],
            offset: 0,
            perPage: 3,
            currentPage: 0,
        }
        this.reserve = this.reserve.bind(this)
        this.fetchData = this.fetchData.bind(this)
    }

    fetchData(){
        axios.get('/library')
          .then(res => {
            const booksData = res.data
            const books = booksData.slice(this.state.offset, this.state.offset + this.state.perPage)
            this.setState({ 
                pageCount: Math.ceil(booksData.length / this.state.perPage),
                books })
          })
    }

    handlePageClick = (e) => {
        const selectedPage = e.selected;
        const offset = selectedPage * this.state.perPage;

        this.setState({
            currentPage: selectedPage,
            offset: offset
        }, () => {
            this.fetchData()
        });
    };

    componentDidMount() {
        this.fetchData()
    }

    render() {
        return (
          <div className="App">
              {/* {this.state.books} */}
            {/* {this.state.booksData.map(book => {
                const {id, title, author, quantity} = book
                return (
                  <div key={id}>
                  <p>{id} - {title} - {author}</p>
                  <button onClick={() => this.reserve(id)}>Reserve {quantity}</button>
                  <span>{this.quantity}</span>
                </div>
                );
            })
            } */}

            {this.state.books && this.state.books.map(book =>
                <React.Fragment key={book.id}>
                <p>{book.id} - {book.title} - {book.author}</p>
                <button onClick={() => this.reserve(book.id)}>Reserve {book.quantity}</button>
                <span>{this.state.booksData.quantity}</span>
                </React.Fragment>
            )}
            <ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={this.state.pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={this.handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}/>
          </div>
        )
    }

    reserve(id) {
        console.log("clicked")
        this.setState({
            booksData: this.state.booksData.map(item => {
                if (item.id === id) {
                    return { ...item, quantity: (item.quantity - 1) >= 0 ? (item.quantity - 1) : 0};
                } else {
                    return item;
                }
            })
        })
    }
}


export default BooksComponent
