import React, { Component } from 'react'
import axios from 'axios';

class BooksComponent extends Component{

    constructor(){
        super()
        this.state ={
            booksData: [],
            offset: 0,
            perPage: 3,
            currentPage: 0,
        }
        this.reserve = this.reserve.bind(this)
    }

    fetchData(){
        axios.get('/library')
          .then(res => {
            const booksData = res.data
            const slice = booksData.slice(this.state.offset, this.state.offset + this.state.perPage)
            const books = slice.map(book => <React.Fragment>
                <p>{book.id}</p>
                <p>{book.title}</p>
                <p>{book.author}</p>
                <p>{book.quantity}</p>
            </React.Fragment>)

            this.setState({ 
                pageCount: Math.ceil(booksData.length / this.state.perPage),
                books })
          })
    }

    componentDidMount() {
        this.fetchData()
    }

    render() {
        return (
          <div className="App">
            {/* {this.state.books.map(book => {
                const {id, title, author, quantity} = book
                return ( */}
                  <div key={this.state.books.id}>
                  <p>{this.state.books.id} - {this.state.books.title} - {this.state.books.author}</p>
                  {/* <button onClick={() => this.reserve(id)}>Reserve {quantity}</button> */}
                  <span>{this.quantity}</span>
                </div>
                {/* );
            })
            } */}
          </div>
        )
    }

    reserve(id) {
        this.setState({
            booksData: this.state.booksData.map(item => {
                if (item.id === id) {
                    return { ...item, quantity: (item.quantity - 1) >= 0 ? (item.quantity - 1) : 0};
                } else {
                    return item;
                }
            })
        })
    }
}


export default BooksComponent
